.player {
	transition: all ease 0.2s;
	width: 100%;
	height: 100%;
	display: flex;
	justify-content: flex-start;
	align-items: center;
	position: relative;
	&:nth-of-type(1) {
		grid-area: p1;
		align-items: center;
		justify-content: flex-end;
		.player {
			&__avatar {
				background-color: rgb(82, 221, 203);
				order: 2;
			}
			&__info {
				order: 1;
				margin-right: 10px;
			}
		}
	}
	&:nth-of-type(2) {
		grid-area: p2;
		.player__avatar {
			background-color: rgb(220, 20, 60);
		}
	}
	&:nth-of-type(3) {
		grid-area: p3;
		.player__avatar {
			background-color: rgb(240, 128, 128);
		}
	}
	&:nth-of-type(4) {
		grid-area: p4;
		.player__avatar {
			background-color: #444;
		}
	}
	&:nth-of-type(5) {
		grid-area: p5;
		.player__avatar {
			background-color: rgb(36, 143, 36);
		}
	}
	&:nth-of-type(6) {
		grid-area: p6;
		.player__avatar {
			background-color: rgb(218, 165, 32);
		}
	}
	&:nth-of-type(7) {
		grid-area: p7;
		.player__avatar {
			background-color: rgb(199, 36, 177);
		}
	}
	&:nth-of-type(8) {
		grid-area: p8;
		.player__avatar {
			background-color: rgb(106, 216, 121);
		}
	}
	&:nth-of-type(9) {
		grid-area: p9;
		.player__avatar {
			background-color: rgb(243, 123, 24);
		}
	}
	&:nth-of-type(10) {
		grid-area: p10;
		.player__avatar {
			background-color: rgb(67, 28, 206);
		}
	}

	&__avatar {
		min-width: 50%;
		height: 0;
		padding-bottom: 50%;
		border-radius: 100%;
		position: relative;
		// box-shadow: 2px 10px 0px rgba(0, 0, 0, 0.4);
		z-index: 1;
		&:after {
			content: "";
			width: 60%;
			height: 60%;
			position: absolute;
			background-color: rgba(0, 0, 0, 0.1);
			top: 50%;
			left: 50%;
			transform: translatex(-50%) translatey(-50%);
			border-radius: 100%;
			box-shadow: 0px 5px 0px rgba(0, 0, 0, 0.2);
		}
	}

	// TODO delete after test
	&__delete {
		position: absolute;
		z-index: 3;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);
		padding: 5px 10px;
		border: rgb(226, 0, 0) 1px solid;
		background-color: rgb(245, 49, 49);
		color: white;
		cursor: pointer;
	}

	&__info {
		border: 1px solid grey;
		border-radius: 10px;
		padding: 10px 20px;
		z-index: 2;
		background-color: #fff;
		flex: auto;
	}
}

